$lightThemeSystem = 0;
$lightThemeApps = 0;
$accentColorOnStart = 0;
$enableTransparency = 0;
$htmlAccentColor = '#0078D4';
& {
	$params = @{
		LiteralPath = 'Registry::HKCU\Software\Microsoft\Windows\CurrentVersion\Themes\Personalize';
		Force = $true;
		Type = 'DWord';
	};
	Set-ItemProperty @params -Name 'SystemUsesLightTheme' -Value $lightThemeSystem;
	Set-ItemProperty @params -Name 'AppsUseLightTheme' -Value $lightThemeApps;
	Set-ItemProperty @params -Name 'ColorPrevalence' -Value $accentColorOnStart;
	Set-ItemProperty @params -Name 'EnableTransparency' -Value $enableTransparency;
};
& {
	Add-Type -AssemblyName 'System.Drawing';
	$accentColor = [System.Drawing.ColorTranslator]::FromHtml( $htmlAccentColor );

	function ConvertTo-DWord {
		param(
			[System.Drawing.Color]
			$Color
		);

		[byte[]] $bytes = @(
			$Color.R;
			$Color.G;
			$Color.B;
			$Color.A;
		);
		return [System.BitConverter]::ToUInt32( $bytes, 0); 
	}

	$startColor = [System.Drawing.Color]::FromArgb( 0xD2, $accentColor );
	Set-ItemProperty -LiteralPath 'Registry::HKCU\Software\Microsoft\Windows\CurrentVersion\Explorer\Accent' -Name 'StartColorMenu' -Value( ConvertTo-DWord -Color $accentColor ) -Type 'DWord' -Force;
	Set-ItemProperty -LiteralPath 'Registry::HKCU\Software\Microsoft\Windows\CurrentVersion\Explorer\Accent' -Name 'AccentColorMenu' -Value( ConvertTo-DWord -Color $accentColor ) -Type 'DWord' -Force;
	Set-ItemProperty -LiteralPath 'Registry::HKCU\Software\Microsoft\Windows\DWM' -Name 'AccentColor' -Value( ConvertTo-DWord -Color $accentColor ) -Type 'DWord' -Force;
	$params = @{
		LiteralPath = 'Registry::HKCU\Software\Microsoft\Windows\CurrentVersion\Explorer\Accent';
		Name = 'AccentPalette';
	};
	$palette = Get-ItemPropertyValue @params;
	$index = 20;
	$palette[ $index++ ] = $accentColor.R;
	$palette[ $index++ ] = $accentColor.G;
	$palette[ $index++ ] = $accentColor.B;
	$palette[ $index++ ] = $accentColor.A;
	Set-ItemProperty @params -Value $palette -Type 'Binary' -Force;
};

$scripts = @(
	{
		Get-Content -LiteralPath 'C:\Windows\Setup\Scripts\GetComputerName.ps1' -Raw | Invoke-Expression > 'C:\Windows\Setup\Scripts\ComputerName.txt';
		Start-Process -FilePath ( Get-Process -Id $PID ).Path -ArgumentList '-NoProfile', '-Command', 'Get-Content -LiteralPath "C:\Windows\Setup\Scripts\SetComputerName.ps1" -Raw | Invoke-Expression;' -WindowStyle 'Hidden';
		Start-Sleep -Seconds 10;
	};
	{
		# Enhanced RMM Agent Detection and Copy (PowerShell 2.0 Compatible)
		$destDir = 'C:\DenkoICT\Download';
		if (!(Test-Path $destDir)) { 
			New-Item -Path $destDir -ItemType Directory -Force | Out-Null;
		};

		$logDir = 'C:\DenkoICT\Logs';
		if (!(Test-Path $logDir)) { 
			New-Item -Path $logDir -ItemType Directory -Force | Out-Null;
		};

		# Log file for debugging (consolidated logs go to Logs folder)
		$logFile = "$logDir\agent-deployment.log";

		# Function to log messages (PS2 compatible)
		function Write-AgentLog {
			param([string]$Message);
			$timestamp = Get-Date -Format 'yyyy-MM-dd HH:mm:ss';
			Add-Content -Path $logFile -Value "[$timestamp] $Message";
		};

		Write-AgentLog "Starting RMM agent search...";

		# Get all drive letters (PS2 compatible method)
		$searchDrives = @();
		$driveLetters = @('C', 'D', 'E', 'F', 'G', 'H', 'I', 'J', 'K');
		foreach ($letter in $driveLetters) {
			$drivePath = "${letter}:\";
			if (Test-Path $drivePath) {
				$searchDrives += $drivePath;
			};
		};

		# Remove C:\ as we don't want to search system drive
		$searchDrives = $searchDrives | Where-Object { $_ -ne 'C:\' };

		Write-AgentLog "Searching drives: $($searchDrives -join ', ')";

		$agentFound = $false;

		foreach ($drive in $searchDrives) {
			Write-AgentLog "Searching in $drive";

			# Search for Agent executable files
			$searchPatterns = @('*Agent*.exe', '*RMM*.exe', '*Datto*.exe', '*N-able*.exe', '*Kaseya*.exe', '*ConnectWise*.exe', '*Ninja*.exe');
			$agentFiles = @();

			foreach ($pattern in $searchPatterns) {
				try {
					$foundFiles = Get-ChildItem -Path $drive -Filter $pattern -ErrorAction SilentlyContinue;
					if ($foundFiles) {
						foreach ($file in $foundFiles) {
							$agentFiles += $file;
						};
					};

					# Search one level deep
					$subDirs = Get-ChildItem -Path $drive -Directory -ErrorAction SilentlyContinue;
					if ($subDirs) {
						foreach ($subDir in $subDirs) {
							$foundFiles = Get-ChildItem -Path $subDir.FullName -Filter $pattern -ErrorAction SilentlyContinue;
							if ($foundFiles) {
								foreach ($file in $foundFiles) {
									$agentFiles += $file;
								};
							};
						};
					};
				} catch {
					Write-AgentLog "Error searching $drive with pattern $pattern : $_";
				};
			};

			if ($agentFiles -and $agentFiles.Count -gt 0) {
				# Take the first found agent
				$agentFile = $agentFiles | Select-Object -First 1;
				$targetPath = "$destDir\Agent.exe";

				Write-AgentLog "Found agent: $($agentFile.FullName)";
				Write-AgentLog "Size: $($agentFile.Length) bytes";
				Write-AgentLog "Copying to: $targetPath";

				try {
					Copy-Item -Path $agentFile.FullName -Destination $targetPath -Force;

					# Verify copy
					if (Test-Path $targetPath) {
						$copiedFile = Get-Item $targetPath;
						if ($copiedFile.Length -eq $agentFile.Length) {
							Write-AgentLog "Successfully copied agent to $targetPath";
							Write-Host "[RMM] Agent copied from $($agentFile.FullName) to $targetPath" -ForegroundColor Green;
							$agentFound = $true;

							# Log agent information directly to the log file
							Write-AgentLog "=== Agent Information ===";
							Write-AgentLog "SourcePath=$($agentFile.FullName)";
							Write-AgentLog "SourceName=$($agentFile.Name)";
							Write-AgentLog "CopyTime=$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss')";
							Write-AgentLog "Size=$($agentFile.Length)";

							break;
						} else {
							Write-AgentLog "ERROR: Copy verification failed - size mismatch";
						};
					} else {
						Write-AgentLog "ERROR: Copy verification failed - file not found";
					};
				} catch {
					Write-AgentLog "ERROR: Failed to copy agent: $_";
				};
			};

			if ($agentFound) { break; };
		};

		if (-not $agentFound) {
			Write-AgentLog "No RMM agent found on any searched drive";
			Write-Host "[RMM] No agent found on removable drives" -ForegroundColor Yellow;

			# Look for installer scripts as fallback
			foreach ($drive in $searchDrives) {
				try {
					$scripts = Get-ChildItem -Path $drive -Filter '*install*.ps1' -ErrorAction SilentlyContinue;
					if ($scripts) {
						foreach ($script in $scripts) {
							if ($script.Name -match 'agent|rmm|remote') {
								Copy-Item -Path $script.FullName -Destination "$destDir\install-rmm.ps1" -Force;
								Write-AgentLog "Copied installation script: $($script.Name)";
								Write-Host "[RMM] Copied installation script: $($script.Name)" -ForegroundColor Cyan;
								break;
							};
						};
					};
				} catch {
					Write-AgentLog "Error searching for scripts in $drive : $_";
				};
			};
		};

		Write-AgentLog "Agent search completed";
	};
	{
		Remove-Item -LiteralPath 'Registry::HKLM\Software\Microsoft\WindowsUpdate\Orchestrator\UScheduler_Oobe\DevHomeUpdate' -Force -ErrorAction 'SilentlyContinue';
	};
	{
		Remove-Item -LiteralPath 'C:\Users\Default\AppData\Roaming\Microsoft\Windows\Start Menu\Programs\OneDrive.lnk', 'C:\Windows\System32\OneDriveSetup.exe', 'C:\Windows\SysWOW64\OneDriveSetup.exe' -ErrorAction 'Continue';
	};
	{
		Remove-Item -LiteralPath 'Registry::HKLM\Software\Microsoft\WindowsUpdate\Orchestrator\UScheduler_Oobe\OutlookUpdate' -Force -ErrorAction 'SilentlyContinue';
	};
	{
		Get-Content -LiteralPath 'C:\Windows\Setup\Scripts\RemovePackages.ps1' -Raw | Invoke-Expression;
	};
	{
		Get-Content -LiteralPath 'C:\Windows\Setup\Scripts\RemoveCapabilities.ps1' -Raw | Invoke-Expression;
	};
	{
		Get-Content -LiteralPath 'C:\Windows\Setup\Scripts\RemoveFeatures.ps1' -Raw | Invoke-Expression;
	};
	{
		net.exe accounts /maxpwage:UNLIMITED;
	};
	{
		reg.exe add "HKLM\Software\Policies\Microsoft\Windows\CloudContent" /v "DisableCloudOptimizedContent" /t REG_DWORD /d 1 /f;
		[System.Diagnostics.EventLog]::CreateEventSource( 'UnattendGenerator', 'Application' );
	};
	{
		Register-ScheduledTask -TaskName 'UnlockStartLayout' -Xml $( Get-Content -LiteralPath 'C:\Windows\Setup\Scripts\UnlockStartLayout.xml' -Raw );
	};
	{
		reg.exe add "HKLM\SYSTEM\CurrentControlSet\Control\FileSystem" /v LongPathsEnabled /t REG_DWORD /d 1 /f
	};
	{
		fsutil.exe behavior set disableLastAccess 1;
	};
	{
		reg.exe add "HKLM\SYSTEM\CurrentControlSet\Control\Session Manager\Power" /v HiberbootEnabled /t REG_DWORD /d 0 /f;
	};
	{
		reg.exe add "HKLM\SOFTWARE\Policies\Microsoft\Dsh" /v AllowNewsAndInterests /t REG_DWORD /d 0 /f;
	};
	{
		reg.exe add "HKLM\Software\Policies\Microsoft\Windows\CloudContent" /v "DisableWindowsConsumerFeatures" /t REG_DWORD /d 1 /f;
	};
	{
		reg.exe add "HKLM\Software\Policies\Microsoft\Edge" /v HideFirstRunExperience /t REG_DWORD /d 1 /f;
	};
	{
		reg.exe add "HKLM\Software\Policies\Microsoft\Edge\Recommended" /v BackgroundModeEnabled /t REG_DWORD /d 0 /f;
		reg.exe add "HKLM\Software\Policies\Microsoft\Edge\Recommended" /v StartupBoostEnabled /t REG_DWORD /d 0 /f;
	};
	{
		Get-Content -LiteralPath 'C:\Windows\Setup\Scripts\SetStartPins.ps1' -Raw | Invoke-Expression;
	};
	{
		reg.exe add "HKU\.DEFAULT\Control Panel\Accessibility\StickyKeys" /v Flags /t REG_SZ /d 10 /f;
	};
);

& {
  [float] $complete = 0;
  [float] $increment = 100 / $scripts.Count;
  foreach( $script in $scripts ) {
    Write-Progress -Activity 'Running scripts to customize your Windows installation. Do not close this window.' -PercentComplete $complete;
    '*** Will now execute command «{0}».' -f $(
      $str = $script.ToString().Trim() -replace '\s+', ' ';
      $max = 100;
      if( $str.Length -le $max ) {
        $str;
      } else {
        $str.Substring( 0, $max - 1 ) + '…';
      }
    );
    $start = [datetime]::Now;
    & $script;
    '*** Finished executing command after {0:0} ms.' -f [datetime]::Now.Subtract( $start ).TotalMilliseconds;
    "`r`n" * 3;
    $complete += $increment;
  }
} *>&1 >> "C:\Windows\Setup\Scripts\Specialize.log";

